import React from 'react'
import Modal from 'react-modal';
import style from './index.css';
import Button from 'react'
import AmtrakEighteenthStreet from "./Photos/18th Street Yards.jpg"

class PopUp extends React.Component {
  constructor() {
    super();

    this.state = { modalOpened: false };
    this.toggleModal = this.toggleModal.bind(this);
  }

  toggleModal() {
    this.setState(prevState => ({ modalOpened: !prevState.modalOpened }));
  }

  render() {
    const { AmtrakEighteenthStreet } = this.props;
    return (
      <div className={style.modalWrapper}>
        <Button handleClick={this.toggleModal}>
          click to open modal
        </Button>
        <Modal
          className={{ base: [style.base]}}
          overlayClassName={{ base: [style.overlayBase] }}
          isOpen={this.state.modalOpened}
          onRequestClose={this.toggleModal}
          contentLabel="Modal with image"
        >
          <img
            onClick={this.toggleModal}
            src={AmtrakEighteenthStreet}
            alt='image displayed in modal'
          />
          <span className={style.text}>photo description.</span>
        </Modal>
      </div>
    );
  }
}

export default PopUp;
